b   = [0.5050056251193388;0.6660900318226777;0.8706318978386682;1.1205463158333715;1.4240741509478785;1.7763273230295336;2.1680578080293857;2.572609278297512;2.958904253989898;3.289727298850572;3.5190110905437626;3.629492529493838;3.619568943406189;3.512137710852133;3.3568761305656936;3.201359582886504;3.0707012266192386;2.976057221761306;2.8945055834683027;2.796115338956522;2.6481139935070024;2.445449022975456;2.1885368631633493;1.8912450279740776;1.589654824487724;1.3007188053226222;1.037263207300549;0.8155312972132264;0.6319386391204354;0.4867418329883126;0.3726328905101162;0.28370517131478334];
L   = [];       % empty L operator refers to the 0-th order regularization.

A = Lcurve( A );

% First quick evaluation of the log-Lcurve and its curvature:
LAMBDAs = 10.^( -19:2:10 );
K = Lcurve( A , b , L , LAMBDAs );

LSplot = @(x,y,varargin)builtin('plot',x,(abs(y).*double(y<0)),'r',x,(abs(y).*double(y>0)),'k',varargin{:});

figure;
subplot(3,3,[1,4]);
  plot(   K(:,1) , K(:,2) ,'.:b'); xlim([-17,27]); ylim([-42,37]); axis('equal');
  for i = ceil( numel(LAMBDAs)*[0.001,0.1,0.3,0.5,0.75,0.9,1] )
    text( K(i,1) , K(i,2) , sprintf(' \\lambda=%.0e',LAMBDAs(i)) ,'VerticalAlignment','bottom','HorizontalAlignment','left','FontSize',8);
    hold on; plot( K(i,1) , K(i,2) ,'ob','MarkerSize',4); hold off
  end
  xlabel('log( | A x - b |^2 )' );
  ylabel('log( | L x |^2 )' );
subplot(3,3,[2,3,5,6]);
  plot(   K(:,1) , K(:,2) ,'o:b'); xlim([-12,6]); ylim([-42,35]); axis('equal');
subplot(3,3,7:9);
  % in red, the negative curvatures, in black, the positive.
  LSplot( LAMBDAs , K(:,3) ,'Marker','o','MarkerEdgeColor','b','LineStyle','-'); set(gca,'XScale','log','YScale','log');
  xlim( [1e-15,1e5] ); ylim( [1e-7 , 10e4 ] );
  xlabel('\lambda' );
  ylabel('Curvature log-Lcurve' );

% Refined evaluation of the log-Lcurve
LAMBDAs = 10.^( -10:0.005:-2 );     % a denser set of lambdas
K = Lcurve( A , b , L , LAMBDAs );

subplot(3,3,[2,3,5,6]); hold on;
  C = reshape( permute( cat(3 , K(:,1:2) , K(:,4:5) , NaN(size(K,1),2) ) ,[3,1,2]) ,[],2);
  plot( C(1:5:end,1) , C(1:5:end,2) ,'-','Color',[0,0,0]+0.7,'XLimInclude','off','YLimInclude','off')
  plot( K(:,1) , K(:,2) ,'k-','LineWidth',2);
  plot( K(:,4) , K(:,5) ,'k-','XlimInclude','off','YlimInclude','off');
  xlim([-10.524,-10.504]); ylim([3.459,3.475]);
  hold off;
subplot(3,3,7:9);       hold on;
  LSplot( LAMBDAs   , K(:,3) ,'LineWidth',2);
  hold off;

% Direct calculation of the knee:
LAMBDA_knee = Lcurve( A , b , L );
K = Lcurve( A , b , L , LAMBDA_knee );
subplot(3,3,7:9);       hold on;
  try, xline( LAMBDA_knee ,':r' ); end
  try, yline( K(1,3)   ,':r'); end
  hold off;

% Interaction
subplot(3,3,[1,4]);
  hP = line( NaN , NaN ,'Marker','o','Color','k','MarkerFaceColor','r','MarkerSize',4);
subplot(3,3,[2,3,5,6]);
  C = linspace(0,2*pi,201).'; C = [ cos(C) , sin(C) ];
  hT = hgtransform();
  line( C(:,1) , C(:,2) ,'Parent', hT ,'Color','r');
  line( 0      , 0      ,'Marker','o','Color','k','MarkerFaceColor','g','MarkerSize',4,'Parent', hT );
  hP(2) = line( NaN , NaN ,'Marker','o','Color','k','MarkerFaceColor','r','MarkerSize',4);
subplot(3,3,7:9);
  hL = line( NaN(2,1) , NaN(2,1) ,'Marker','o','Color','m');
update_ = @(K      )[ set( hT , 'Matrix', [eye(3)/abs(K(3)),[K(4:5)';0];0,0,0,1] ) ; set( hP ,'XData', K(1),'YData', K(2)) ; set(hL,'YData',[1e-9,abs(K(3))]) ];
update  = @(LAMBDA )[ true ; update_( Lcurve( A , b , L ,LAMBDA) ) ; set(hL,'XData',[0,0]+LAMBDA) ];
update( LAMBDA_knee );
set( ancestor(hL,'figure') , 'WindowButtonMotionFcn' , @(h,e)isequal(ancestor(hittest(),'axes'),get(hL,'Parent')) && update( mean( get( get(hL,'Parent') ,'CurrentPoint' ))*[1;0;0] ) )

